import React, { useState, useEffect, ChangeEvent, KeyboardEvent } from "react";
import { Button, Form, Col, Row } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import Select from "react-select";

import api from "../../../services/api";

import "./index.css";

//inferces para facilitar a manipulação dos dados
interface IDoctor {
  id: string;
  nome: string;
  crm: number;
  telfixo: number;
  telcelular: number;
}

type OptionType = {
  value: string;
//  label: string;
};

interface IAdress {
  id: string;
  cepForm: string;
  logradouro: string;
  complemento: string;
  bairro: string;
  localidade: string;
  uf: string;
}

interface Icep {
  cep: string;
}
interface ISpecialties {
  specialties: string;
}

const listSpecialties = [
  "Alergologia",
  "Angiologia",
  "Buco maxilo",
  "Cardiologia clínca",
  "Cardiologia infantil",
  "Cirurgia cabeça e pescoço",
  "Cirurgia cardíaca",
  "Cirurgia de tórax",
];

//dados para o dropdowns
const SpecialtiesOptions: OptionType[] = [
  { value: "Alergologia"                   },
  { value: "Angiologia"                    },
  { value: "Buco maxilo"                   },
  { value: "Cardiologia clínca"            },
  { value: "Cardiologia infantil"          },
  { value: "Cirurgia cabeça e pescoço"     },
  { value: "Cirurgia cardíaca"             },
  { value: "Cirurgia de tórax"             },
];

//REACT
const Doctors: React.FC = () => {
  var SpecialtiesSelect;
  useEffect(() => {
    loadSpecialties();
  });

  const history = useHistory();

  //useState
  const [cep, setCep] = useState<Icep>({
    cep: "",
  });

  const [specialties, setSpecialties] = useState<ISpecialties>({
    specialties: "",
  });

  const [adress, setAdress] = useState<IAdress>({
    id: "",
    cepForm: "",
    logradouro: "",
    complemento: "",
    bairro: "",
    localidade: "",
    uf: "",
  });

  const [doctor, setDoctor] = useState<IDoctor>({
    id: "",
    nome: "",
    crm: 0,
    telfixo: 0,
    telcelular: 0,
  });

  //cadastra especialidades no banco se não existirem
  async function loadSpecialties() {
    const getSpecialties = await api.get("/Specialties");

    if (getSpecialties.data.length == 0) {
      listSpecialties.forEach((index) => {
        registerSpecialties(index);
      });
    }

    SpecialtiesSelect = getSpecialties.data;
  }

  async function registerSpecialties(specialties: string) {
    const responseSpecialties = await api.post("Specialties", {
      name: specialties,
    });
  }

  //funcao responsavel por consultar o cep
  async function GetCep(this: any, e: ChangeEvent<HTMLInputElement>) {
    setCep({
      ...cep, //garante que o input só recebera numeros
      [e.target.name]: e.target.value.replace(/\D/g, ""),
    });
    let lengthCep = e.target.value.replace(/\D/g, "").length;
    let cepNumber = e.target.value.replace(/\D/g, "");

    //valida se o usuario digitou os 8 digitos cep para poder fazer a requisicao
    if (lengthCep == 8) {
      const resp = await api.post("/CEP/" + cepNumber + "");

      setAdress({
        id: "",
        cepForm: resp.data.cep == undefined ? "" : resp.data.cep,
        logradouro:
          resp.data.logradouro == undefined ? "" : resp.data.logradouro,
        complemento:
          resp.data.complemento == undefined ? "" : resp.data.complemento,
        bairro: resp.data.bairro == undefined ? "" : resp.data.bairro,
        localidade:
          resp.data.localidade == undefined ? "" : resp.data.localidade,
        uf: resp.data.uf == undefined ? "" : resp.data.uf,
      });
    }
  }

  function UpdatedSpecialties(e:any) {
    // setSpecialties({
    //   ...specialties,
    //   [e.target.name]: e.target.value,
    // });

    console.log(e)
  }
  function UpdatedDoctor(e: ChangeEvent<HTMLInputElement>) {
    setDoctor({
      ...doctor,
      [e.target.name]: e.target.value,
    });
  }
  function UpdatedAdress(e: ChangeEvent<HTMLInputElement>) {
    setAdress({
      ...adress,
      [e.target.name]: e.target.value,
    });
  }

  function onSubmit(e: ChangeEvent<HTMLFormElement>) {
    e.preventDefault();

    console.log(doctor);
    console.log(adress);
  }

  function Back() {
    history.goBack();
  }

  return (
    <div className="container">
      <br />
      <div className="Header-doctors">
        <h1>Cadastro</h1>
        <Button variant="dark" size="sm" onClick={Back}>
          Voltar
        </Button>
      </div>
      <br />
      <div className="container">
        <Form onSubmit={onSubmit}>
          <Form.Group>
            <Form.Label>Nome</Form.Label>
            <Form.Control
              maxLength={120}
              type="text"
              name="nome"
              onChange={(e: ChangeEvent<HTMLInputElement>) => UpdatedDoctor(e)}
            />
          </Form.Group>
          <br />
          <Form.Group>
            <Row>
              <Col>
                <Form.Label>Tel. Celular</Form.Label>
                <Form.Control
                  placeholder="(81)88888-8888"
                  type="text"
                  name="telcelular"
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedDoctor(e)
                  }
                />
              </Col>
              <Col>
                <Form.Label>Tel. Fixo</Form.Label>
                <Form.Control
                  placeholder="(81)88888-8888"
                  type="text"
                  name="telfixo"
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedDoctor(e)
                  }
                />
              </Col>
              <Col>
                <Form.Label>CRM</Form.Label>
                <Form.Control
                  maxLength={7}
                  type="text"
                  name="crm"
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedDoctor(e)
                  }
                />
              </Col>
            </Row>
            <br />
            <Form.Label>Especialidades</Form.Label>
            <Select
              isMulti
              name="specialties"
              options={SpecialtiesOptions}
              className="basic-multi-select"
              classNamePrefix="Selecione"
              onChange={(e: any) => UpdatedSpecialties(e)}
            />
          </Form.Group>
          <br />
          <h3>Cadastro de Endereço</h3>

          <Form.Group>
            <Row>
              <Col>
                <Form.Label>CEP</Form.Label>
                <Form.Control
                  type="text"
                  name="cep"
                  maxLength={8}
                  placeholder="Digite o CEP"
                  value={cep.cep}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => GetCep(e)}
                />
              </Col>
              <Col>
                <Form.Label>Cidade</Form.Label>
                <Form.Control
                  placeholder=""
                  type="text"
                  name="localidade"
                  value={adress.localidade}
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedAdress(e)
                  }
                />
              </Col>
              <Col>
                <Form.Label>Estado</Form.Label>
                <Form.Control
                  placeholder=""
                  type="text"
                  name="uf"
                  value={adress.uf}
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedAdress(e)
                  }
                />
              </Col>
            </Row>
            <Row>
              <Col>
                <Form.Label>Logradouro</Form.Label>
                <Form.Control
                  placeholder=""
                  type="text"
                  name="logradouro"
                  value={adress.logradouro}
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedAdress(e)
                  }
                />
              </Col>
              <Col>
                <Form.Label>Bairro</Form.Label>
                <Form.Control
                  placeholder=""
                  type="text"
                  name="bairro"
                  value={adress.bairro}
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedAdress(e)
                  }
                />
              </Col>
              <Col>
                <Form.Label>Complemento</Form.Label>
                <Form.Control
                  placeholder=""
                  type="text"
                  name="uf"
                  value={adress.complemento}
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    UpdatedAdress(e)
                  }
                />
              </Col>
            </Row>
          </Form.Group>
          <br />
          <Button variant="dark" type="submit">
            Salvar
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default Doctors;
