import React, { useState, useEffect } from "react";
import { Button, Form, Col, Row } from "react-bootstrap";
import Select from "react-select";

import api from "../../../services/api";

import "./index.css";

interface IDoctor {
  ID: string;
  NOME: string;
  CRM: number;
  TELFIXO: number;
  TELCELULAR: number;
  BAIRRO: string;
  UF: string;
  LOCALIDADE: string;
}

